class hotelfarecal:    def __init__(self,rt='',s=0,p=0,r=0,t=0,a=10,name='',address='',cindate='',coutdate='',rno=101):        print ("\n\n*****HOTEL BILLING SYSTEM*****\n")        self.rt=rt        self.r=r        self.t=t        self.p=p        self.s=s        self.a=a        self.name=name        self.address=address        self.cindate=cindate        self.coutdate=coutdate        self.rno=rno    def inputdata(self):        self.name=input("\nEnter your name:")        self.address=input("\nEnter your address:")        self.cindate=input("\nEnter your check in date:")        self.coutdate=input("\nEnter your checkout date:")        print("Your room no.:",self.rno,"\n")            def roomrent(self):        print ("We have the following rooms for you:-")        print ("1.  type A---->$50 PN\-")        print ("2.  type B---->$40 PN\-")        print ("3.  type C---->$30 PN\-")        print ("4.  type D---->$20 PN\-")        x=int(input("Enter Your Choice Please->"))        n=int(input("For How Many Nights Did You Stay:"))        if(x==1):            print ("you have opted room type A")            self.s=50*n        elif (x==2):            print ("you have opted room type B")            self.s=40*n        elif (x==3):            print ("you have opted room type C")            self.s=30*n        elif (x==4):            print ("you have opted room type D")            self.s=20*n        else:            print ("please choose a room")        print ("your room rent is =",self.s,"\n")    def restaurentbill(self):        print("*****RESTAURANT MENU*****")        print("1.Juice----->$1","2.Tea----->$2","3.Breakfast Meal--->$5","4.Lunch Meal---->$7","5.Dinner Meal--->$8","6.Exit")        while (1):            c=int(input("Enter your choice:"))            if (c==1):                d=int(input("Enter the quantity:"))                self.r=self.r+1*d            elif (c==2):                 d=int(input("Enter the quantity:"))                 self.r=self.r+2*d            elif (c==3):                 d=int(input("Enter the quantity:"))                 self.r=self.r+5*d            elif (c==4):                 d=int(input("Enter the quantity:"))                 self.r=self.r+7*d            elif (c==5):                 d=int(input("Enter the quantity:"))                 self.r=self.r+8*d            elif (c==6):                break;            else:                print("Invalid option")        print ("Total food Cost=$",self.r,"\n")    def	laundrybill(self):        print ("******LAUNDRY MENU*******")        print ("1.Shorts----->$1","2.Trousers----->$1","3.Shirt--->$2","4.Jeans---->$2","5.Girlsuit--->$2","6.Exit")        while (1):            #brought to you by code-projects.org            e=int(input("Enter your choice:"))            if (e==1):                f=int(input("Enter the quantity:"))                self.t=self.t+1*f            elif (e==2):                f=int(input("Enter the quantity:"))                self.t=self.t+1*f            elif (e==3):                f=int(input("Enter the quantity:"))                self.t=self.t+2*f            elif (e==4):                f=int(input("Enter the quantity:"))                self.t=self.t+2*f            elif (e==5):                f=int(input("Enter the quantity:"))                self.t=self.t+2*f            elif (e==6):                break;            else:                print ("Invalid option")        print ("Total Laundary Cost=$",self.t,"\n")    def gamebill(self):        print ("******GAME MENU*******")        print ("1.Table Tennis----->$5","2.Bowling----->$5","3.Billiard--->$6","4.Video Games---->$2","5.Pool--->$5","6.Exit")        while (1):                        g=int(input("Enter your choice:"))            if (g==1):                h=int(input("No. of hours:"))                self.p=self.p+5*h            elif (g==2):                h=int(input("No. of hours:"))                self.p=self.p+5*h            elif (g==3):                h=int(input("No. of hours:"))                self.p=self.p+6*h            elif (g==4):                h=int(input("No. of hours:"))                self.p=self.p+2*h            elif (g==5):                h=int(input("No. of hours:"))                self.p=self.p+5*h            elif (g==6):                break;            else:                print ("Invalid option")        print ("Total Game Bill=$",self.p,"\n")    def display(self):        print ("******Total BILL******")        print ("Customer details:")        print ("Customer name:",self.name)        print ("Customer address:",self.address)        print ("Check in date:",self.cindate)        print ("Check out date",self.coutdate)        print ("Room no.",self.rno)        print ("Your Room rent is:",self.s)        print ("Your Food bill is:",self.r)        print ("Your laundary bill is:",self.t)        print ("Your Game bill is:",self.p)        self.rt=self.s+self.t+self.p+self.r        print ("Your sub total bill is:",self.rt)        print ("Additional Service Charges is",self.a)        print ("Your grandtotal bill is:",self.rt+self.a,"\n")        self.rno+=1                            def main():    a=hotelfarecal()        while (1):        print("1.Create Customer Detail")                print("2.Rent a Room")        print("3.Order Food")        print("4.Laundry")        print("5.Game Station")        print("6.Total Bill")        print("7.EXIT")        b=int(input("\nEnter your choice:"))        if (b==1):            a.inputdata()        if (b==2):            a.roomrent()        if (b==3):            a.restaurentbill()        if (b==4):            a.laundrybill()        if (b==5):            a.gamebill()        if (b==6):            a.display()        if (b==7):            quit()main()